mapper.delete:
delete
from bill
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.update:
update bill
set item_id = #{itemId,jdbcType=INTEGER},
    money   = #{money,jdbcType=INTEGER}
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select date, user_id, item_id, money
from bill
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}
