mapper.delete:
delete
from confirm
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.update:
update confirm
set confirm_date = #{confirmDate,jdbcType=TIMESTAMP},
    confirm_flag = #{confirmFlag,jdbcType=INTEGER}
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select date, user_id, confirm_date, confirm_flag
from confirm
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}
