mapper.delete:
delete
from error_rec
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.update:
update error_rec
set error_id   = #{errorId,jdbcType=INTEGER},
    `before`     = #{before,jdbcType=DOUBLE},
    after      = #{after,jdbcType=DOUBLE},
    check_time = #{checkTime,jdbcType=TIME},
    error_flag = #{errorFlag,jdbcType=INTEGER}
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select date, user_id, error_id, `before`, after, check_time, error_flag
from error_rec
where date = #{date,jdbcType=DATE}
  and user_id = #{userId,jdbcType=VARCHAR}
